        function saveThemePreference() {
            preference.theme = preference.theme === 'light' ? 'dark' : 'light';

            setThemePreference();
        };

        function saveContrastPreference(param = 'no-preference') {
            preference.contrast = param;
            setContrastPreference();
        };

        function saveColorPreference() {
            preference.color = document.querySelector('#app-color-theme').value;

            setColorPreference();
        };

        function getThemePreference() {
            if (localStorage.getItem('theme-preference')) {
                return localStorage.getItem('theme-preference')
            }
            else {
                return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
            }
        };

        function getContrastPreference() {
            if (localStorage.getItem('contrast-preference')) {
                return localStorage.getItem('contrast-preference')
            }
            else {
                return window.matchMedia('(prefers-contrast: more)').matches ? 'more' :
                window.matchMedia('(prefers-contrast: less)').matches ? 'less' : 'no-preference';
            }
        };

        function getColorPreference() {
            if (localStorage.getItem('color-preference')) {
                return localStorage.getItem('color-preference');
            }
            else {
                return Math.floor(Math.random() * (1 + 360 - 0)) + 0;
            }
        };

        function setThemePreference() {
            localStorage.setItem('theme-preference', preference.theme);

            reflectThemePreference();
        };

        function setContrastPreference() {
            localStorage.setItem('contrast-preference', preference.contrast);

            reflectContrastPreference();
        };

        function setColorPreference() {
            localStorage.setItem('color-preference', preference.color);

            reflectColorPreference();
        };

        function reflectThemePreference() {
            document.firstElementChild.setAttribute('data-theme', preference.theme);

            var themeToggle = document.getElementById('theme-toggle');

            if (themeToggle) {
                themeToggle.setAttribute('aria-label', preference.theme);
            }
        };

        function reflectContrastPreference() {
            document.firstElementChild.setAttribute('data-contrast', preference.contrast);
        };

        function reflectColorPreference() {
            document.documentElement.style.setProperty('--clr-hue', preference.color);

            var colorSlider = document.getElementById('app-color-theme');

            if (colorSlider) {
                colorSlider.value = preference.color;
            };
        };

        function updateColorPreference(hue) {
            document.documentElement.style.setProperty('--clr-hue', hue);
        };

        function shuffleColorPreference() {
            localStorage.removeItem('color-preference');
            preference.color = getColorPreference();

            reflectColorPreference();
        };

        var preference = {
            theme: getThemePreference(),
            color: getColorPreference(),
            contrast: getContrastPreference()
        };

        reflectThemePreference();
        reflectContrastPreference();
        reflectColorPreference();

        window.addEventListener("DOMContentLoaded", () => {
            reflectThemePreference();
            reflectContrastPreference();
            reflectColorPreference();

            document.getElementById('theme-toggle').addEventListener('click', saveThemePreference);
            document.getElementById('contrast-less').addEventListener('click', function() {saveContrastPreference('less')});
            document.getElementById('contrast-normal').addEventListener('click', function() {saveContrastPreference('no-preference')});
            document.getElementById('contrast-more').addEventListener('click', function() {saveContrastPreference('more')});
            document.getElementById('save-app-color-theme').addEventListener('click', saveColorPreference);
            document.getElementById('shuffle-app-color-theme').addEventListener('click', shuffleColorPreference);
            document.getElementById('app-color-theme').addEventListener('input', (event) => {
                updateColorPreference(event.target.value);
            });
        });

        window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', ({ matches: isDark }) => {
            preference.theme = isDark ? 'dark' : 'light';

            setThemePreference();
        });

        window.matchMedia('(prefers-contrast: more)').addEventListener('change', ({ matches: isMore }) => {
            preference.theme = isMore ? 'more' : 'no-preference';

            setContrastPreference();
        });

        window.matchMedia('(prefers-contrast: less)').addEventListener('change', ({ matches: isLess }) => {
            preference.theme = isLess ? 'less' : 'no-preference';

            setContrastPreference();
        });

