        function debounce(command, milisecondDelay) {
          let timer;

          return function (...argument) {
            clearTimeout(timer);
            timer = setTimeout(() => {
                command(...argument);
            }, milisecondDelay);
          };
        };

        function showHideTopAppBarOnScroll(element, milisecondDelay) {
          let lastWindowScrollPosition = 0;

          let updateClass = (targetElement) => {

            let currentWindowSrollPosition = window.scrollY;

            if (currentWindowSrollPosition <= 0) {
              targetElement.classList.remove('scrolled-up');
              targetElement.classList.remove('scrolled-down');
            }

            if (currentWindowSrollPosition > lastWindowScrollPosition && !targetElement.classList.contains('scrolled-down')) {
              targetElement.classList.remove('scrolled-up');
              targetElement.classList.add('scrolled-down');
            }

            if (currentWindowSrollPosition < lastWindowScrollPosition && targetElement.classList.contains('scrolled-down')) {
              targetElement.classList.remove('scrolled-down');
              targetElement.classList.add('scrolled-up');
            }

            lastWindowScrollPosition = currentWindowSrollPosition;

          };

          updateClass = debounce(updateClass, milisecondDelay);

          window.addEventListener('scroll', () => updateClass(element), { passive: true });
        };

        function handleGlobalClickEvent() {
          window.addEventListener('click', (event) => {
            if (event.target.closest('nav a')) {
              event.stopPropagation();
              closeMainMenu();
            }

            if (event.target.closest('.comment-action a')) {
              event.preventDefault();
              event.stopPropagation();
              displayReplyForm(event);
            }
          });
        };

        function closeMainMenu() {
          document.getElementById('main-menu-button').click();
        };

        function displayReplyForm(event) {
          var replyForm = document.getElementById('reply-form');
          if (replyForm) {replyForm.remove();};
          var iframe = document.createElement('iframe');
          iframe.id = 'reply-form';
          iframe.src = event.target.href;
          event.target.parentNode.appendChild(iframe);
        };

        function adjustFormHeight(event) {
          if (event.origin == 'https://www.blogger.com') {
            var replyIframe = document.getElementById('reply-form');

            if (replyIframe) {replyIframe.height = event.data.substring(26);};
          };
        };

        showHideTopAppBarOnScroll(document.body, 500);
        handleGlobalClickEvent();
        window.addEventListener('message', () => adjustFormHeight(event), { passive: true });

