          function getRelatedPosts(feedData) {
            const currentPostUrl = "";
            const maxResults = 4;
            const containerTitle = "Artikel Serupa";
            const snippetLength = 160;

            let relatedTitles = [];
            let relatedUrls = [];
            let thumbUrls = [];
            let snippets = [];

            /* Extract post data from feed */
            for (let entry of feedData.feed.entry) {
              let title = entry.title.$t;
              let summary = entry.summary.$t;
              let thumbUrl;

              /* Try to get thumbnail, fallback to default */
              try {
                thumbUrl = entry.media$thumbnail.url.replace(/(s72-c|s72-rc)/g, "w232-h174-p-k-no-nu");
              } catch {
                thumbUrl = "";
              }

              /* Truncate summary if too long */
              if (summary.length > snippetLength) {
                summary = summary.substring(0, snippetLength) + "...";
              }

              /* Find the "alternate" link (the post URL) */
              let postUrl = null;
              for (let link of entry.link) {
                if (link.rel === "alternate") {
                  postUrl = link.href;
                  break;
                }
              }

              /* Only add if we found a post URL */
              if (postUrl) {
                relatedTitles.push(title);
                relatedUrls.push(postUrl);
                thumbUrls.push(thumbUrl);
                snippets.push(summary);
              }
            }

            /* Remove duplicates */
            const uniqueUrls = [];
            const uniqueTitles = [];
            const uniqueThumbs = [];
            const uniqueSnippets = [];
            for (let i = 0; i < relatedUrls.length; i++) {
              if (!uniqueUrls.includes(relatedUrls[i])) {
                uniqueUrls.push(relatedUrls[i]);
                uniqueTitles.push(relatedTitles[i]);
                uniqueThumbs.push(thumbUrls[i]);
                uniqueSnippets.push(snippets[i]);
              }
            }
            relatedUrls = uniqueUrls;
            relatedTitles = uniqueTitles;
            thumbUrls = uniqueThumbs;
            snippets = uniqueSnippets;

            /* Remove current post and any empty titles */
            for (let i = 0; i < relatedUrls.length; i++) {
              if (relatedUrls[i] === currentPostUrl || !relatedTitles[i]) {
                relatedUrls.splice(i, 1);
                relatedTitles.splice(i, 1);
                thumbUrls.splice(i, 1);
                snippets.splice(i, 1);
                i--;
              }
            }

            /* Render related posts */
            const container = document.getElementById("related-posts");
            if (!container) return;
            if(!relatedTitles.length) return;

            const titleElem = document.createElement("h2");
            titleElem.id = "related-posts-header";
            titleElem.className = "title";
            titleElem.appendChild(document.createTextNode(containerTitle));

            const listElem = document.createElement("div");
            listElem.id = "related-posts";

            /* Shuffle start index for randomness */
            let startIdx = Math.floor(Math.random() * relatedTitles.length);

            for (let i = 0, count = 0; count < maxResults && count < relatedTitles.length; count++) {
              const idx = (startIdx + count) % relatedTitles.length;
              const url = relatedUrls[idx];
              const title = relatedTitles[idx];
              const thumb = thumbUrls[idx];
              const snippet = snippets[idx];

              const svg = document.createElementNS("http://www.w3.org/2000/svg", "svg");
              svg.setAttribute("viewBox", "0 0 24 24");
              svg.setAttribute("aria-hidden", "true");

              const use = document.createElementNS("http://www.w3.org/2000/svg", "use");
              use.setAttributeNS("http://www.w3.org/1999/xlink", "href", "");
              svg.appendChild(use);

              const a = document.createElement("a");
              a.className = "item-url";
              a.href = url;
              a.title = title;
              a.appendChild(svg);

              const titleP = document.createElement("div");
              titleP.className = "item-title";
              titleP.appendChild(document.createTextNode(title));

              const snippetP = document.createElement("div");
              snippetP.className = "item-snippet";
              snippetP.appendChild(document.createTextNode(snippet));

              const itemP = document.createElement("div");
              itemP.className = "item";
              const contentP = document.createElement("div");
              contentP.className = "content";
              contentP.appendChild(titleP);
              contentP.appendChild(snippetP);
              itemP.appendChild(contentP);
              itemP.appendChild(a);

              const li = document.createElement("div");
              li.className = "card";

              if (thumb !== "") {
                const img = document.createElement("img");
                img.alt = title;
                img.title = title;
                img.src = thumb;
                img.loading = "lazy";

                li.appendChild(img);
              }

              li.appendChild(itemP);
              listElem.appendChild(li);
            }

            container.replaceWith(titleElem);
            const refNode = document.getElementById("related-posts-header");
            refNode.parentNode.insertBefore(listElem, refNode.nextSibling);

            /* Clear arrays for next use */
            relatedTitles.length = 0;
            relatedUrls.length = 0;
            thumbUrls.length = 0;
            snippets.length = 0;
          };
          
          function getNextPrevTitle(url, node) {
            var xhr = new XMLHttpRequest();
            xhr.open("GET", url, true);
            xhr.responseType = "document";
          
            xhr.onload = function() {
              if (this.readyState == 4 && this.status == 200) {
                var respDocument = this.responseXML;
                var title = respDocument.querySelector("article h1").textContent;
                var span = document.createElement("span");
                span.className = "pagination-title";
                span.appendChild(document.createTextNode(title));
                node.appendChild(span);
              }
            };

            xhr.onerror = function() {
                console.error("AJAX request failed.");
            };

            xhr.send();
          }
          
          !(function () {
              var prevButton = document.querySelector("a.prev-page.single");
              var nextButton = document.querySelector("a.next-page.single");
              if (prevButton) getNextPrevTitle(prevButton.href, prevButton);
              if (nextButton) getNextPrevTitle(nextButton.href, nextButton);
          })();
          
